mapper.select:
INSERT INTO reservation (user_id, book_id, deadline)
VALUES (#{user.userId}, (SELECT book_id
                         FROM book
                         WHERE ISBN =
                               #{ISBN}
                         LIMIT 1), NULL);

select borrow_id, bname, btime, deadline, rtime
from borrow_detail
where user_id = #{user.userId}

mapper.update:
update borrow
SET rtime = NOW()
WHERE borrow_id = #{borrowId}
